import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { IProduct } from '../prodsharemod/models/IProduct';
import { IBrands} from '../prodsharemod/models/IBrands';
import { ProdshopmodService } from './prodshopmod.service';
import { ShopParameters } from '../prodsharemod/models/shopParameters';
import { UserAccountService } from '../Account/account.service';

@Component({
  selector: 'app-shop',
  templateUrl: './prodshopmod.component.html',
  styleUrls: ['./prodshopmod.component.scss']
})
export class ProdshopmodComponent implements OnInit {
  @ViewChild('search') searchText?: ElementRef;
  products:IProduct[]=[];
  brands:IBrands[]=[];
  productTypes:IBrands[]=[];
  shopParameters: ShopParameters;
  totalPageNumber=0;
  notice : boolean = false;
  errors : string[] | null = null;
  advertsString : string="Adverts or notifications will appear here";
  sortingOptions=[
    {name:'Alphabeltical', value:'name'},
    {name:'Price: High to Low', value:'priceDecrease'},
    {name:'Price: Low to High', value:'priceIncrease'}
  ];

  constructor(
    private prodshopmodService: ProdshopmodService, 
    public accountService:UserAccountService) 
    {
    this.shopParameters = prodshopmodService.getShopParams();
  }

  ngOnInit(){
    this.GetProductTypes();
    this.GetProductBrands();
    this.GetProducts();
    this.GetAdverts();

  }

  GetProducts(){
      this.prodshopmodService.getProducts().subscribe({
         next: response=>{ 
          console.log(response)
           this.products=response.data; 
           this.totalPageNumber=response.count;
          },
      error: error => console.log(error)

  })
}

  GetProductBrands(){


        this.prodshopmodService.getBrands().subscribe({
          next: response=>{ 
            console.log(response)
                this.brands=[{id:0,name:'All'}, ...response];         
          },
          error:error=>console.log(error)         
  });
}

  GetProductTypes(){
        this.prodshopmodService.getProductTypes().subscribe({
          next: response=>{ 
              this.productTypes=[{id:0,name:'All'}, ...response];;
            },
            error:error=>console.log(error)   
  });
} 

  SelectedBrand(brandId:number){
    const params = this.prodshopmodService.getShopParams();
    params.brandId=brandId;
    params.pageNumber=1;
    this.prodshopmodService.setShopParams(params);
    this.shopParameters=params;
    this.GetProducts();
}

  SelectedType(typeId:number){
    const params = this.prodshopmodService.getShopParams();
    params.typeId=typeId;
    params.pageNumber=1;
    this.prodshopmodService.setShopParams(params);
    this.shopParameters=params;
    this.GetProducts();
}

SortedProducts(event:any){
  const params = this.prodshopmodService.getShopParams();
  params.sort=event.target.value;
  this.prodshopmodService.setShopParams(params);
  this.shopParameters=params;
this.GetProducts();
}

PageChange(event:any){
  const params = this.prodshopmodService.getShopParams();
if(params.pageNumber!==event){ 
  params.pageNumber=event;
  this.prodshopmodService.setShopParams(params);
  this.shopParameters=params;
this.GetProducts();
  }
}

Search(){
  const params = this.prodshopmodService.getShopParams();
  params.search=this.searchText?.nativeElement.value;
  params.pageNumber=1;
  this.prodshopmodService.setShopParams(params);
  this.shopParameters=params;
this.GetProducts();
}

ResetSearch(){
  if (this.searchText)
  this.searchText.nativeElement.value='';
  this.shopParameters = new ShopParameters();
  this.prodshopmodService.setShopParams (this.shopParameters);
  this.GetProducts();
  }

  GetAdverts(){
      this.prodshopmodService.GetAdverts().subscribe({
        next : adverts => {
          if(adverts)
          if(adverts.length>0) this.notice = true;
          for(let x of adverts){
            if(adverts.length>1){
              this.advertsString = x.advert+" | ".slice(0,-2);

            }
            else{
              this.advertsString = x.advert
            }

          }
     },
       error : error => { 
         this.errors = error.errors
       }

      });
  }
}
<div class="container body" >
           <div class="row content">
               <!-- Section1 -->
                <section class="col-1  left-section">
                      <h5><strong style="color:black ; font-weight:bold;">Mission</strong> </h5>
                      <p>Our mission is to make sure we statify our customer
                        needs by putting smiles on their faces after business 
                        transctions with us.
                      </p>
                      <hr>
                      <div style="width: auto;height:auto;">
                        <app-product-adverts></app-product-adverts>
                      </div>
                      <hr>
                      <h5><strong style="color:black ; font-weight:bold;">Policy</strong></h5>
                      <p>Our policy is unique and is geared towards making you 
                        happy for the rest of your life..
                      </p>   
                </section>


             <!-- Section2 -->
                <section class=" pb-1 col-9 text-center">
                  <div *ngIf="(accountService.AppUser$|async) ==null" >
                    <div class="marquee">
                      <Marquee scrollamount="3">Welcome to Obande unique bag collections. 
                      We are delighted that you chose us. You can make your choice from the best collections 
                      you can find around the world....
                      <strong style="color: red;">*IMPORTANT: </strong>{{advertsString}}
                    </Marquee></div>
                    </div>
                  <div *ngIf="(accountService.AppUser$|async) as user" >
                  <div class="marquee2" *ngIf="totalPageNumber!=0">
                    <Marquee scrollamount="3">
                    <strong>Hi {{user.nickName}}!!.</strong> 
                    <strong *ngIf="notice == false">
                    Thank you for viewing our items. 
                    There are loads of these items you can ever imagined instore you might not find here. 
                   Give us a call today. Your commitment is the 
                    reason we have upgraded to serve you better.
                    </strong >
                      <strong *ngIf="notice == true" style="color: red;"> *NOTICE/ADVERT(S): </strong>{{advertsString}}
                     </Marquee>
                  </div>
                  </div>
                  <div *ngIf="(accountService.AppUser$|async) as user" >
                    <div class="marquee2" *ngIf="totalPageNumber==0">
                      <Marquee scrollamount="3">
                      Sorry {{user.nickName}}. We are really sorry for any incovieniences 
                      not finding your product right now might have cost. We might be 
                      short in supply but will still take your orders and deliver it to 
                      your door step if you want. Better still, check if your spellings 
                      are correct if you are using the search option. Thanks.</Marquee>
                    </div>
                    </div>

                    <ng-container *ngIf="products">
                      <div class="container item-container" >
                        <div class="card-container mb-4 " *ngFor="let item of products">
                              <app-product-item-child [product]="item"></app-product-item-child>
                      </div>
                      <div class="noResult" *ngIf="totalPageNumber==0">
                            <strong><p style="color:red">Sorry!!!.. </p>
                            <p>No result for this search can be found.</p>
                            <p>You can reload page to bring back products items.</p> 
                            <p>Better still, click a brand and/or type.</p>
                            </strong>
                          </div>
                        </div>
                      </ng-container>

            <div class="d-grid justify-content-center mt-3" *ngIf="totalPageNumber&&totalPageNumber>0">
                  <app-pagination-shared-componet
                  [totalPageNumber]="totalPageNumber"
                  [pageSize]="shopParameters.pageSize"
                  [pageNumber]="shopParameters.pageNumber"
                  (pageChange)="PageChange($event)">
                  ></app-pagination-shared-componet>
                <div style="color:blue ; font-weight:bolder">
                  <app-page-analyser 
                  [totalPageNumber]="totalPageNumber"
                  [pageNumber]="shopParameters.pageNumber"
                  [pageSize]="shopParameters.pageSize">
                  </app-page-analyser>
                </div>  
                </div>
            </section>


   <!-- Section3 -->
    <!-- Search -->
    <section class="col-2 right-section-search">
      <ng-container *ngIf="products">
          <form class="d-flex ml-3 search" >
            <input (keyup.enter)="Search()" type="text" class="form-control me-2 search-text" #search  placeholder="Products" >
            <button (click)="Search()" class="btn btn-outline-success bg-primary text-light search-btn">Search</button>
            <button (click)="ResetSearch()" class="btn btn-outline-success bg-primary text-light search-btn" >Reset</button>
          </form>
       </ng-container>

       <!-- Sort-->
      <h5>Sort Products</h5>
      <select class="form-select" (change)="SortedProducts($event)">
        <option *ngFor="let item of sortingOptions"
        [selected]="shopParameters.sort==item.value"
        [value]="item.value"
        >{{item.name}}</option>
      </select>

       <ng-container *ngIf="brands&&productTypes">
         <!-- Brands -->
        <div class="right">
        <h5>Brands</h5>
          <div class="right-section" >   
              <ul class="list-group">
                 <li class="list-group-item"
                    *ngFor="let item of brands"
                    [class.active]="item.id === shopParameters.brandId" 
                    [value]="item.id"
                    (click)="SelectedBrand(item.id)">
                    {{item.name}}
                  </li>
              </ul>
           </div>
          </div> 
       <!-- Types -->
       <div class="right">
       <h5>Types</h5>
       <div class="right-section" >    
          <ul class="list-group">
                <li class="list-group-item"
                *ngFor="let item of productTypes"
                [class.active]="item.id === this.shopParameters.typeId" 
                [value]="item.id"
                (click)="SelectedType(item.id)"> 
                {{item.name}} 
              </li>
          </ul> 
       </div>
      </div>
   </ng-container>
 </section>
 </div>
</div>
