import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { UserAccountService } from '../Account/account.service';
import { BasketService } from '../basket/basket.service';
import { IProduct } from '../prodsharemod/models/IProduct';
import { ProdshopmodService } from './prodshopmod.service';
import { environment } from 'environments/environment';

@Component({
  selector: 'app-product-item-child',
  templateUrl: './product-item-child.component.html',
  styleUrls: ['./product-item-child.component.scss']
})
export class ProductItemChildComponent implements OnInit{

  @Input() product?: IProduct;
  adminEmail = environment.adminEmail;


  constructor(private basketService: BasketService,
    public accountService:UserAccountService,
    private productsService : ProdshopmodService,
    private toastr : ToastrService){
  }

  ngOnInit(): void {


  }



  AddItem(){ 
    this.product && this.basketService.AddItemsToBasket(this.product,1)
  }
  DeletItem(){
    this.productsService.DeleteProduct(this.product.id).subscribe(
        deleted=>{
          if(deleted==true){
           window.location.reload();
          this.toastr.success("Product deleted") ;
          }
        }
    );
  }


}
<div class="card cardItems">
    <img routerLink="/products/{{product.id}}" src={{product.prodPicture}} 
    class="card-img-top img-fluids cardImage"  alt={{product.prodName}}>
    <strong class="productname">{{product.prodName}}</strong>
    <div class="card-body">
      <strong class="pricing">£{{product.prodPrice}}</strong>

      <div class="d-flex justify-content-center">

      <button (click)="AddItem()" class="btnOrder">
      <strong class="ak">Buy</strong>
      </button>

        <button routerLink="/products/{{product.id}}" class="btnOrder">
         <strong class="ak">View</strong>
        </button>

        <ng-container *ngIf="(accountService.AppUser$|async) as user" >
          <button *ngIf="user.email==adminEmail" (click)="DeletItem(product.id)" 
          class="btnOrder"><strong class="ak">Delete</strong></button>
           </ng-container>
        </div>
    </div>
  </div>
