  GetAdverts(){
      this.advert.getAdverts().subscribe({
        next : advert => {

          if(advert)
              this.advertsString = advert ;

              if(advert.length == 0){
                this.exit = 0; 
                this.notice = true;
              } 

              for(let x of advert) {  
                this.exit = x.time; 
                setInterval(()=>{      
                this.deleteAdvert(x.id);
                       },x.time*1000);
            }
        },
          error : error => { 
            this.errors = error.errors
            this.toastr.success("Can't get advert Ids");
          }
      });
      }

      deleteDelivery(id:number){
