import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { LoginComponent } from '../Account/login/login.component';
import { Basket,IBasketItem } from '../prodsharemod/models/IBasket';
import { BasketService } from './basket.service';
@Component({
  selector: 'app-basket',
  templateUrl: './basket.component.html',
  styleUrls: ['./basket.component.scss']
})
export class BasketComponent {
  loginStatus = false;

  constructor(
    public basketService: BasketService,
     private matdialog:MatDialog){
  }
  IncreaseQuantity(items:IBasketItem){
    this.basketService.AddItemsToBasket(items);
  }
  ReduceQuantity(event:{id:number,quantity:number}){
   this.basketService.RemoveItemsFromBasket(event.id,event.quantity);

  }

  checkLogin(){
        if(localStorage.getItem("login")=="true")
        return true;
  }

  openLoginDialog(){
    this.matdialog.open(LoginComponent,
      {height: 'auto',
    width: '50%'});
  }

  deleteBasket(basket:Basket){
      this.basketService.DeleteBasket(basket);
  }

}

<div class="container">
<div *ngIf="(basketService.basket$|async)===null">
    <strong style="color: red; font-size:30px; margin-top:300px">
        Sorry!! Basket is now empty...</strong>   
 </div> 
<div class="container mt-10" style="margin-top:110px; margin-left:50px;">
    <ng-container  *ngIf="(basketService.basket$|async) as basket">
        <marquee class="mb-4 fst-italic">
            Thank you for you interest in our product.  
             Your feedback is important to us for improvement...
            </marquee>
            <div class="container">
            <div class="row col-12 mb-5" style="margin: 0 auto;">
                <app-basket-summary
                (addItem)="IncreaseQuantity($event)"
                (removeItem)="ReduceQuantity($event)"
                >
              </app-basket-summary>
            </div>

            <div class="row align-items-center">
                <div class="row col-12" style="width:60% ;">
                    <app-order-total></app-order-total>
                    </div>
                    <div *ngIf="basket!=null" class="d-flex mt-5 mb-5" > 
                        <button  class="checkout" (click)="deleteBasket(basket)" 
                        style="background-color: red;" >
                            <strong style="color: white; font-size:auto;">Clear basket</strong> 
                        </button>
                        <button routerLink="/Checkout" class="checkout"  style="background-color: green;">
                            <strong style="color:white; font-size:auto;">Proceed to checkout</strong>
                         </button>
                    </div>
                </div>
                </div>


    </ng-container>
</div>
</div>
